"use strict";

var mongoose = require('mongoose'); // Erase if already required

// Declare the Schema of the Mongo model
var productSchema = new mongoose.Schema({
  title: {
    type: String,
    required: true,
    trim: true
  },
  slug: {
    type: String,
    required: true,
    unique: true,
    lowercase: true
  },
  description: {
    type: String,
    required: true
  },
  price: {
    type: Number,
    required: true
  },
  category: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "category"
  },
  brand: {
    type: String,
    "enum": ["Lenovo", "Apple", "Samsung"]
  },
  sold: {
    type: Number,
    "default": 0
  },
  quantity: {
    type: Number,
    required: true
  },
  image: {
    type: Array
  },
  color: {
    type: String,
